import React from "react";
import {Link,} from "react-router-dom";
import { BasketItemType } from "../../../Components/BasketItemType";
import { MyButtonGrid } from "../../../Components/Buttons/Buttons";
import { ItemWrapper } from "../../../Components/Item/Item.styles";
import bestselling from '../../../Images/bestselling.png'

type Props = {
  item: BasketItemType;
  handleAddToCart: (clickedItem: BasketItemType) => void;
};
<div className="product-img">
<img src={data.img_path} alt={data.name}></img>
<h6><span>{data.category}</span></h6>
</div>

const PopItem: React.FC<Props> = ({ item, handleAddToCart}) => (
  <ItemWrapper>
      <div className="pop">
    <Link to={{pathname: `${item.productId}`, state: {productObject: item}}}>
    <img src={item.img_path} alt={item.name} />
    <span></span><img src={bestselling}/>
    </Link>
    </div>
    <div>
      <h3>{item.name}</h3>
      <p>{item.description}</p>
      <h4>{item.price} DKK</h4>
    </div>
    <MyButtonGrid onClick={() => handleAddToCart(item)}>Add to cart</MyButtonGrid>
  </ItemWrapper>
);

export default PopItem;
