import React, { useState } from "react";
import { BrowserRouter, NavLink, Route, Switch } from "react-router-dom";
import { LoginForm } from "./Excercise";

export interface Login {
    firstName: string,
    lastName: string,
    email: string,
  }

  const init = {
    firstName: "",
    lastName: "",
    email: ""
  }
export interface IFormContext {
    form: Login;
    updateForm: (firstName:string, lastName:string, email:string) => void
  }

// create context, but there is no default value - set it to undefined.
export const FormContext = React.createContext<IFormContext | undefined>(
    undefined
  );

  export function App(){
    const [form, updateForm] = useState<Login>(init)
    const updateFormFun = (firstName: string, lastName: string, email: string) => {

        updateForm((prev) => ({...prev, ...{email:email, firstname:firstName, lastName:lastName}})) 
      }

      return (
        <FormContext.Provider value={{ form: form, updateForm:updateFormFun }}>
          <div className="App">
            <BrowserRouter>
              <div style={{ display: "flex" }}>
                <div className="App-header">
                  <ul style={{ listStyleType: "none" }}>
                    <li>
                      <NavLink exact activeClassName="active" to="/">
                        Home
                      </NavLink>
                    </li>
                    <li>
                      <NavLink activeClassName="active" to="/quiz">
                        Quiz
                      </NavLink>
                    </li>
                    <li>
                      <NavLink activeClassName="active" to="/statistics">
                        Statistics
                      </NavLink>
                    </li>
                    <li>
                      <NavLink activeClassName="active" to="/editquiz">
                        Edit Quiz
                      </NavLink>
                    </li>
                    <li>
                      <NavLink activeClassName="active" to="/about">
                        About
                      </NavLink>
                    </li>
                  </ul>
                </div>
                <Switch>
                  <Route path="/editquiz" component={() => <LoginForm/>} />
                  <Route path="/" component={Home} />
                </Switch>
              </div>
            </BrowserRouter>
          </div>
        </FormContext.Provider>
      );

  }

  function Home() {
    return <h2>Home</h2>;
  }
