import styled from 'styled-components';

export const NavigationWrapper = styled.aside`

ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
    overflow: hidden;

  }



  .topNav a:hover{
      color:rgb(143, 140, 140);
  }

  nav li {
    float: left;
  }

  nav li a {
    display: block;
    color: rgb(49, 48, 48);
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
    font-size: 20px;
  }

  .bottomNav li a:hover {
    background-color: rgb(176, 187, 170);
  }

  body{
      margin:0%;
  }


`;
