import React from 'react';
import logo from "../Images/logo.png";
/* import '../App.css'; */
import '../style.css';
import '../navigation.css';
import TopPicture from './topPictures';
import Carousel from './carousel';
import PictureBanner from './pictureBanner';

import {
  BrowserRouter,
  NavLink,
  Route,
  Switch,
  useHistory,
  useParams,
  Link,
} from "react-router-dom";

class NavBar extends React.Component {
    render() {
      return (
        <div className="App">
          <BrowserRouter>
          <header>
          <Link className='logos' to='/'>
          <img className="logo" src={logo} alt="Hot Liquids" />
        </Link> 
          <nav>
                <div className='topnav-right'>
                <ul className="topNav">
                  <li>
                    <NavLink activeClassName="active" to="/login">
                      Login
                    </NavLink>
                  </li>
                  <li>
                    <NavLink activeClassName="active" to="/basket">
                      Basket
                    </NavLink>
                  </li>
                </ul>
                </div>
                <ul className="bottomNav">
                  <li>
                    <NavLink exact activeClassName="active" to="/">
                      Home
                    </NavLink>
                  </li>
                  <li>
                    <NavLink activeClassName="active" to="/coffees">
                      Coffees
                    </NavLink>
                  </li>

                  <li>
                    <NavLink activeClassName="active" to="/teas">
                      Teas
                    </NavLink>
                  </li>
                </ul>

                <Switch>
                <Route exact path="/" component={Home} />
                <Route path="/coffees" component={Coffees} />
                <Route path="/teas" component={Teas} />
                <Route path="/login" component={Login} />
                <Route path="/basket" component={Basket} />
                </Switch>
                </nav>
                </header>
        </BrowserRouter>
      </div>
    );
  }
}

function Home() {
    return (
        <div>
          <TopPicture/>
          <Carousel/>
          <PictureBanner/>
        </div>
      );
  }

  function Login() {
    return (
      <div>
        <TopPicture/>
      </div>
    );
  }

  function Coffees() {
    return <h2>Coffees</h2>;
  }

  function Teas() {
    return <h2>Teas</h2>;
  }

  function Basket() {
    return <h2>Basket</h2>;
  }


export default NavBar
