import React from "react";
import { useState } from "react";
import { useQuery } from "react-query";
import { CartItemType } from "./allproducts";

export const BasketContext = React.createContext([[], () => {}])

export const BasketState = (props: any) => {
  const [cartItemss, setCartItems] = useState([] as CartItemType[]);
  React.useEffect(() => {
    fetch('http://localhost:3000/customers/1/basketDetails')
      .then((response) => response.json())
      .then((cartItemss) => {
        setCartItems(cartItemss);
      })
  }, [cartItemss]);

  return (
    <BasketContext.Provider value={[cartItemss, setCartItems]} {...props} />
  );
};
