// Types
import { CartItemType } from '../CartItemType';
import {MyButtonGrid} from '../../App';
// Styles
import { Wrapper } from './Item.styles';
import './Item.css';
import '../../Pages/style.css';
import {
  Link,
} from "react-router-dom";

type Props = {
  item: CartItemType;
  handleAddToCart: (clickedItem: CartItemType) => void;
};

const Item: React.FC<Props> = ({ item, handleAddToCart}) => (

    <div className="content">
    <div className="product-image">
            <a className="links" href="productDescription.html">
            <img src={item.img_path}></img></a></div>
            <div className="product-info">
            <h2 className="name">{item.name}</h2>
                <p className="des">{item.description}</p>
                <p className="price">Price: ${item.price} DKK</p>
                <button className="btn add" onClick={() => handleAddToCart(item)}>ADD TO CART</button>
                <MyButtonGrid onClick={() => handleAddToCart(item)}>Add to cart</MyButtonGrid>
            </div>    
        </div>

);

export default Item;
