import React, { useState } from "react";
import { BrowserRouter, NavLink, Route, Switch } from "react-router-dom";
import { LoginForm } from "./LoginForm";

export interface Login {
    firstName: string,
    lastName: string,
    email: string,
  }

  const init = {
    firstName: "",
    lastName: "",
    email: ""
  }
export interface IFormContext {
    form: Login;
    updateForm: (firstName:string, lastName:string, email:string) => void
  }

// create context, but there is no default value - set it to undefined.
export const FormContext = React.createContext<IFormContext | undefined>(
    undefined
  );

  export function App(){
    const [form, updateForm] = useState<Login>(init)
    const updateFormFun = (firstName: string, lastName: string, email: string) => {
        updateForm((prev) => ({...prev, ...{email:email, firstname:firstName, lastName:lastName}})) 
      }

      return (
        <FormContext.Provider value={{ form: form, updateForm:updateFormFun }}>
          <LoginForm/>
        </FormContext.Provider>
      );

  }

  function Home() {
    return <h2>Home</h2>;
  }
