import React, { useContext } from 'react';
import { withStyles } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox, { CheckboxProps } from '@material-ui/core/Checkbox';
import { BoxContext, checkBoz } from './CheckBoxContext';

const boxContext = useContext(BoxContext)

if (!boxContext)
  throw(new Error("FormContext is undefined!"))
  let checkedA = boxContext.checkBox.checkedA;
  let checkedB = boxContext.checkBox.checkedB;
  let checkedC = boxContext.checkBox.checkedC;
  const [state, setCheckBoxes] = React.useState<checkBoz>({ checkedA: checkedA, checkedB: checkedB, checkedC: checkedC});

  const handleC = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCheckBoxes((prev) => ({ ...prev, ...{ email: event.target.value } }));
};

export function CheckBox(){
  return (
    <FormGroup row>
      <FormControlLabel
        control={
        <Checkbox
        checked={state.checkedA}
        onChange={handleC}
        name="checkedA" />}
        label="Secondary"
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={state.checkedB}
            onChange={handleC}
            name="checkedB"
            color="primary"
          />
        }
        label="Primary"
      />
       <FormControlLabel
        control={
         <Checkbox
            checked={state.checkedC}
            onChange={handleC}
            name="checkedC"
            color="primary"
        />
     }
        label="Primary"
 />
    </FormGroup>
  );
}
