import React, { useState, useEffect } from "react";
import { useQuery } from "react-query";
import { Link, BrowserRouter as Router, Route } from "react-router-dom";
import { CartItemType } from "./allproducts";

const getProducts = async (): Promise<CartItemType[]> =>
  await (await fetch('http://localhost:3000/products/info')).json();


const Prod = (productId: any) => {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState([] as CartItemType[]);
  useEffect(() => {
    fetch(`http://localhost:3000/products/${productId}`, {})
      .then((res) => res.json())
      .then((response) => {
        setData(response);
        setIsLoading(false);
        console.log(`http://localhost:3000/products/${productId}`)
      })
      .catch((error) => console.log(error));
  }, [productId]);

  return (
    data.map((i)=> {
      let item = document.createElement('div');
          item.className = 'product';
          item.innerHTML =
          `<div class="product-img">
          <img id = "image" src="${i.img_path}">
          </div>
          <div class="product-des">
          <h1 id="name">${i.name}</h1>
          <h3 id="price">Price: ${i.price} DKK</h3>
          <p id="des">${i.description}</p>
          <button class="btn add">Add to cart</button>
          </div>`
        }));
      }

