// Types
import { BasketItem } from '../CartItemType';
import {MyButtonGrid} from '../../Components/Buttons/Buttons';
// Styles
import { Wrapper } from './Item.styles';
/* import './Item.css'; */
import {
  Link,
} from "react-router-dom";

type Props = {
  item: BasketItem;
  handleAddToCart: (clickedItem: BasketItem) => void;
};

const Item: React.FC<Props> = ({ item, handleAddToCart}) => (
  <Wrapper>
    <Link to={{pathname: `${item.productId}`, state: {productObject: item}}}>
    <img src={item.img_path} alt={item.name} />
    </Link>
    <div>
      <h3>{item.name}</h3>
      <p>{item.description}</p>
      <h4>{item.price} DKK</h4>
    </div>
    <MyButtonGrid onClick={() => handleAddToCart(item)}>Add to cart</MyButtonGrid>
  </Wrapper>
);

export default Item;
