import CartItem from '../CartItem/CartItem';
import { Wrapper } from './Cart.styles';
import { CartItemType } from '../CartItemType';
import { useContext, useState } from 'react';
import { FormContext } from '../../Pages/Login/FormContext';
import {FormData} from '../../Pages/Login/LoginForm';

export type Props =  {
  cartItems: CartItemType[];
  addToCart: (clickedItem: CartItemType) => void;
  removeFromCart: (id: number) => void;
  removeFromCart2: (id: number) => void;
};

const init = {
  firstName: "",
  lastName: "",
  email: ""
}

const Cart: React.FC<Props> = ({ cartItems, addToCart, removeFromCart, removeFromCart2 }) => {
  const calculateTotal = (items: CartItemType[]) =>
    items.reduce((ack: number, item) => ack + item.quantity * item.price, 0);
    const [state, setState] = useState<FormData>(init);

    const  formContext = useContext(FormContext)
    if (!formContext)
      throw(new Error("FormContext is undefined!"))



    let firstName = formContext.form.firstName;
    let lastName = formContext.form.lastName;
    let email = formContext.form.email;

  return (
    <Wrapper>
      <h2>Your Shopping Cart {firstName}</h2>
      {cartItems.length === 0 ? <p>No items in cart.</p> : null}
      {cartItems.map(item => (
        <CartItem
          key={item.productId}
          item={item}
          addToCart={addToCart}
          removeFromCart={removeFromCart}
          removeFromCart2={removeFromCart2}
        />
      ))}
      <h2>Total: ${calculateTotal(cartItems).toFixed(2)}</h2>
    </Wrapper>
  );
};

export default Cart;
