}

const getTotalItems = (items: CartItemType[]) =>
  items.reduce((ack: number, item) => ack + item.quantity, 0);

  const NavBar = () => {
    const [cartOpen, setCartOpen] = useState(false);

  const [cartItems, setCartItems] = useState([] as CartItemType[]);
  React.useEffect(() => {
  fetch('http://localhost:3000/customers/1/basketDetails')
    .then((response) => response.json())
    .then((cartItems) => {
      setCartItems(cartItems);
    })
}, [cartItems]);

      return (
        <div className="App">

          <Drawer anchor='right' open={cartOpen} onClose={() => setCartOpen(false)}></Drawer>
          <BrowserRouter>

          <header>
          <Link className='logos' to='/'>
          <img className="logo" src={logo} alt="Hot Liquids" />
        </Link> 
          <nav>
                <div className='topnav-right'>
                <ul className="topNav">
                  <li>
                    <NavLink activeClassName="active" to="/login">
                      Login
                    </NavLink>
                  </li>
                  <li>
                  <StyledButton onClick={() => setCartOpen(true)}>
                  <Badge badgeContent={getTotalItems(cartItems)} color='error'>
                  <AddShoppingCartIcon />
                  </Badge>
 </StyledButton>
                  </li>
                </ul>
                </div>
                <ul className="bottomNav">
                  <li>
                    <NavLink exact activeClassName="active" to="/">
                      Home
                    </NavLink>
                  </li>
                  <li>
                  <NavLink exact activeClassName="active" to="/allproducts">
                  All products
                  </NavLink>
                  </li>
                  <li>
                    <NavLink activeClassName="active" to="/coffees">
                      Coffees
                    </NavLink>
                  </li>

                  <li>
                    <NavLink activeClassName="active" to="/teas">
                      Teas
                    </NavLink>
                  </li>
                </ul>
                <Switch>
                <Route exact path="/" component={Home} />
                <Route path="/allproducts" component={AllProducts} />
                <Route path="/coffees" component={Coffee} />
                <Route path="/teas" component={Tea} />
                <Route path="/login" component={Login} />
                <Route path="/basket" component={Basket} />
                <Route exact path="/id" component={ProductDescription} />
                </Switch>
                </nav>
                </header>
        </BrowserRouter>
      </div>
    );
  }


function Home() {
    return (
        <div>
          <TopPicture/>
          <Carousel/>
          <PictureBanner/>
        </div>
      );
  }

  function Login() {
    return (
      <div>
        <UserName/>
        <SignUp/>
      </div>
    );
  }

  function AllProducts() {
    return (
      <div>
       <ProductList/>
      </div>
    );
  }

  function Coffee() {
    return (
      <div>
        <Coffees/>
      </div>
    );
  }

  function Tea() {
    return (
      <div>
       <Teas/>
      </div>
    );
  }

  function Basket() {
    return <h2>Basket</h2>;
  }

  function ProductDescription() {
    return (
      <div>
       <Prod/>
      </div>
    );
  }

