import React, { useState, useEffect } from "react";
import { BrowserRouter, Link, match, NavLink, Route, Switch, useParams } from 'react-router-dom';
import { useQuery } from "react-query";
import { Wrapper } from "../App.styles";
import { MyButtonGrid } from "../App";
import handleAddToCart from "./allproducts";
import Grid from "@material-ui/core/Grid";


export type CartItemType = {
    productId: number;
    category: string;
    description: string;
    img_path: string;
    price: number;
    name: string;
    quantity: number;
    longDescription: string;
  };

  type Props = {
    item: CartItemType;
    handleAddToCart: (clickedItem: CartItemType) => void;
  };

  const Item: React.FC<Props> = ({ item, handleAddToCart}) => (
    <Wrapper>
      <img src={item.img_path} alt={item.name} />
      <div>
        <h3>{item.name}</h3>
        <p>{item.description}</p>
        <h3>${item.price}</h3>
      </div>
      <MyButtonGrid onClick={() => handleAddToCart(item)}>Add to cart</MyButtonGrid>
    </Wrapper>
  );

const ProductDetails = ({match}: {match: any}) => {
      const getProduct = async (): Promise<CartItemType> =>
      await (await fetch('http://localhost:3000/products/'+ match.params.productId)).json(); 
      const {data} = useQuery<CartItemType>(
        'product',
        getProduct
      );

      if(data){
        console.log(data.name)
      }

return (
  <Wrapper>
     <Grid item key={data.productId} xs={12} sm={3}>
   <Item item={item} handleAddToCart={handleAddToCart} />
 </Grid>
  <img src={data?.img_path} alt={data?.name} />
  <div>
    <h3>{data?.name}</h3>
    <p>{data?.description}</p>
    <p>{data?.longDescription}</p>
    <h3>${data?.price}</h3>
  </div>
  {/* <MyButtonGrid onClick={() => handleAddToCart(data)}>Add to cart</MyButtonGrid> */}
</Wrapper>
)
}

export default ProductDetails
